import React, {Component} from 'react'
import ImageGallery from 'react-image-gallery'
import "react-image-gallery/styles/css/image-gallery.css"
import './LandingPage.css'
const IMG_BASE_URL = 'http://image.tmdb.org/t/p/original/'

// the page after login, maybe some large images of new films and recommendations for the user?
class LandingPage extends Component {
    constructor(props) {
        super(props);

        this.state = {
            newReleases: [],
            loading: true
        }
    }


    componentDidMount() {
        console.log(new Date())
        if(this.state.loaded !== new Date()) { // Only get new data if the component have not gotten any data today
            console.log("New day since last update, let's update!")
            this.getNewMovies()
        }
    }

    getNewMovies = () => {
        this.props.model.getMoviesInTheatre()
            .then(data => {
                const movies = data.results.slice(0,5) // get the 5 first movies
                this.setState({
                    loading: false,
                    newReleases: movies.map(movie => ({
                        id: movie.id,
                        title: movie.title,
                        overview: movie.overview,


                        originalTitle: movie.id, // the id instead of title so that we can get the movie from the api if clicked
                        original: IMG_BASE_URL + movie.backdrop_path, // image
                        description: movie.title + ', release date: ' + movie.release_date, // shown on picture
                    }))
                })
            })
            .catch(e => console.log(e))
    }

    handleClick = (e) => {
        console.log(e.target.title)
    }

    render() {

        const images = [
            {
                original: 'https://boygeniusreport.files.wordpress.com/2018/04/avengers-infinity-war3.jpg?quality=98&strip=all',
                originalTitle: 'Avengers',
                description: 'Avengers Endgame, Out 2019/03/19'
            },
            {
                original: 'http://scifiward.com/wp-content/uploads/2018/07/alita-battle-angel-et00066939-09-12-2017-02-10-47.jpg',
                originalTitle: 'Alita',
                description: 'Alita battle angel, Out 2019/03/19'
            },
        ]

        return (
            <div className="container appContainer">
                {this.props.navbar}

                <h4 className="titles">Newest releases</h4>
                {this.state.loading && "LOADING..."}
                {!this.state.loading &&
                    <ImageGallery items={this.state.newReleases} showThumbnails={false} showFullscreenButton={false}
                              autoPlay={true} showPlayButton={false} onClick={this.handleClick} showNav={false}/>
                }

                <h4 className="titles">Recommended for you</h4>
                <p className="left">Baserat p√• nuvarande listor....</p>

            </div>
        );
    }
}


export default LandingPage;
